import { useState } from 'react';
import '../styles/App.scss';

function App() {
  const [ingredients, setIngredients] = useState(false);

  const handleIngredients = (ev) => {
    setIngredients(ev.target.checked);
  };
  return (
    <div>
      <h1>Selecciona los ingredientes de la tortilla de patatas</h1>
      <form>
        <label htmlFor="ingredients">Macarrones</label>
        <input
          type="checkbox"
          name="ingredients"
          id="ingredients"
          checked={ingredients}
          onChange={handleIngredients}
        />
        <label htmlFor="ingredients">Patatas</label>
        <input
          type="checkbox"
          name="ingredients"
          id="ingredients"
          checked={ingredients}
          onChange={handleIngredients}
        />
        <label htmlFor="ingredients">Nueces</label>
        <input
          type="checkbox"
          name="ingredients"
          id="ingredients"
          checked={ingredients}
          onChange={handleIngredients}
        />
        <label htmlFor="ingredients">Huevos</label>
        <input
          type="checkbox"
          name="ingredients"
          id="ingredients"
          checked={ingredients}
          onChange={handleIngredients}
        />
        <label htmlFor="ingredients">Cebolla</label>
        <input
          type="checkbox"
          name="ingredients"
          id="ingredients"
          checked={ingredients}
          onChange={handleIngredients}
        />
        <label htmlFor="ingredients">Cerveza</label>
        <input
          type="checkbox"
          name="ingredients"
          id="ingredients"
          checked={ingredients}
          onChange={handleIngredients}
        />
      </form>
    </div>
  );
}

export default App;
