import { useState } from 'react';
import '../styles/App.scss';

function App() {
  const [ingredients, setIngredients] = useState('Macarrones');

  const handleIngredients = (ev) => {
    setIngredients(ev.target.checked);
  };
  return (
    <div>
      <h1>Selecciona los ingredientes de la tortilla de patatas</h1>
      <form>
        <label htmlFor="ingredients">
          ¿Qué ingredientes usas para la tortilla?
        </label>
        <input
          type="checkbox"
          name="ingredients"
          id="ingredients"
          checked={ingredients}
          onChange={handleIngredients}
        />
        <p>{ingredients === true ? 'Sí' : 'No'} Macarrones</p>
      </form>
    </div>
  );
}

export default App;
